#!/bin/bash

assetIds=(10415117542 10147037856 10415219629 10415230769 10415269179 10415100931)

rm -rf assets/images/**

for id in ${assetIds[@]}; do
  url="https://assetdelivery.roblox.com/v1/asset/?id=$id"
  curl -L --cookie ".ROBLOSECURITY=_|WARNING:-DO-NOT-SHARE-THIS.--Sharing-this-will-allow-someone-to-log-in-as-you-and-to-steal-your-ROBUX-and-items.|_" $url --output assets/images/$id.png
  sleep 1
done
